import UIKit

extension UIColor {
	class var lpGrapefruit: UIColor {
		return UIColor(red: 255.0 / 255.0, green: 86.0 / 255.0, blue: 86.0 / 255.0, alpha: 1.0)
	}

	class var lpBlack: UIColor { 
		return UIColor(white: 3.0 / 255.0, alpha: 1.0)
	}

	class var lpPinkishGrey: UIColor { 
		return UIColor(white: 193.0 / 255.0, alpha: 1.0)
	}

	class var lpCoolGrey: UIColor {
		return UIColor(red: 164.0 / 255.0, green: 170.0 / 255.0, blue: 179.0 / 255.0, alpha: 1.0)
	}
}
